$(document).ready(function() {
    var $element = $('#bubble');
    var phrases = [
        'Résumé',
        ];
    var index = -1;
    (function loopAnimation() {
        index = (index + 1) % phrases.length;
        bubbleText({
            element: $element,
            newText: phrases[index],
            letterSpeed: 90,
            callback: function() {
                setTimeout(loopAnimation, 100000);
            },
        });
    })();
});